import { CartItem } from "./CartItem";

function CartView(props){

    const {cart, onAdd, onDelete} = props;

    const cartItems = () =>{

        for(let newItem of cart){
            <CartItem key = {newItem.id} id = {newItem.id} cartItemInfo = {newItem} onAdd = {onAdd} onDelete = {onDelete}></CartItem>
        }

    }


    return(
        <div className = 'cartView'>
            <h2>-- Cart --</h2>
            {cartItems}
        </div>
    )

}

export {CartView}
