import { useState } from 'react'
import { LinkButton } from '../pages/LinkButton';

import '../styles/header.css'

function Header(){

    const [showCart, setShowCart] = useState(false)

    function openCart(){
        if(showCart){
            setShowCart(!showCart);

        }else{
            setShowCart(!showCart);

        }
    }

    return(

        <div className = 'header'>
            <h1>STORE NAME</h1>
            <h2><LinkButton link = '/' text = 'Home'></LinkButton></h2>
            <h2><LinkButton link = '/store' text = 'Store'></LinkButton></h2>
            <h2><LinkButton link = '/about' text = 'About'></LinkButton></h2>
            <h2 onClick = {openCart}>Cart</h2>
        </div>

    )
}
export {Header}
