import { useState } from 'react'
import { RouteSwitch } from "../pages/RouteSwitch";
import { Header } from "./Header";
import { CartView } from "./cart/CartView";
import { items } from './store/items';


function Main(){

    const [cart, setCart] = useState([]);

    function addItemToCart(id){

        setCart(function(prevState){
            const newItems = items.map(function(newItem){
                if(id === newItem.id){
                    if(!itemInCart(newItem)){
                        newItem.cartCount = 1;
                        cart.push(newItem);
                    }else{
                        newItem.cartCount++;
                    }
                }

            }  )
            return([...cart]);
        })

        console.log(cart)
    }
    function itemInCart(item){
        for(let x of cart)
            if(x === item)
                return true;

        return false;
    }

    function handleAdd(id){
        cart.map(function(newItem){
            if(id === newItem.id)
                newItem.cartCount++;
            return newItem;
        })

        setCart([...cart])

    }
    function handleRemove(id){

        cart.map(function(newItem){
            if(id === newItem.id){
                if(newItem.cartCount == 1){
                    console.log('doing')
                    setCart(function(prevState){
                        prevState.filter(function(newItem){
                            return id !== newItem.id
                        })
                    })
                }
                else 
                    newItem.cartCount--;
            }

        })

        setCart([...cart])

    }

    // function handleDeleteSchool(id){
    //     setCv(function(prevState){

    //         const schoolItems = prevState.schoolInfo.filter(function(newItem){
    //             return id !== newItem.id;
    //         })
    //         return({...prevState, schoolInfo: [...schoolItems]});
    //     })
    // }


    return (
        <div className = 'main'>


            <RouteSwitch addItemToCart = {addItemToCart}></RouteSwitch>

            <CartView cart = {cart} onAdd = {handleAdd} onRemove = {handleRemove}></CartView>
        </div>
    )
}

export {Main}
