import { useState } from 'react'
import { RouteSwitch } from "../pages/RouteSwitch";
import { Header } from "./Header";
import { CartView } from "./cart/CartView";
import { items } from './store/items';


function Main(){

    const [cart, setCart] = useState([]);

    function addItemToCart(id){

        setCart(function(prevState){
            const newItems = items.map(function(newItem){
                if(id === newItem.id){
                    if(itemInCart(newItem)){
                        newItem.cartCount++;
                    }
                    if(!itemInCart(newItem)){
                        cart.push(newItem);
                        newItem.cartCount = 1;
                    }

                }
                return newItem;
            }  )
            return([...prevState]);
        })

        console.log(cart)
    }
    function itemInCart(item){
        for(let x of cart)
            if(x === item)
                return true;

        return false;
    }

    function handleAdd(id){
        cart.map(function(newItem){
            if(id === newItem.id)
                newItem.id++;
            return newItem;
        })

    }
    function handleRemove(){

    }

    return (
        <div className = 'main'>


            <RouteSwitch addItemToCart = {addItemToCart}></RouteSwitch>

            <CartView cart = {cart} onAdd = {handleAdd} onRemove = {handleRemove}></CartView>
        </div>
    )
}

export {Main}
