            if(id === newItem.id){
                console.log(newItem.name);
            }
        })

    }

    return(
        <div className = 'store'>
            <h1>Store</h1>

            <ListItems list = {items} onClick = {addItemToCart}></ListItems>
        </div>
    )

}
