import { useEffect, useState } from 'react'

import '../styles/header.css'

const cartButton = document.querySelector('#cartButton');

function Header(){

    const [showCart, setShowCart] = useState(false)

    useEffect(function openCart(){
        if(showCart){
            setShowCart(!showCart);

        }else{
            setShowCart(!showCart);

        } 

        cartButton.addEventListener(openCart)
        }

        ,[showCart])

    return(

        <div className = 'header'>
            <h1>STORE NAME</h1>
            <h1>Home</h1>
            <h1>Store</h1>
            <h1>About</h1>
            <h1 className = 'cartButton'>Cart</h1>
        </div>

    )
}
export {Header}
