import { RouteSwitch } from '../pages/RouteSwitch';

import '../styles/header.css'

function Header(){

    const [showCart, setShowCart] = useState(false)

    function openCart(){
        if(showCart){
            setShowCart(!showCart);

        }else{
            setShowCart(!showCart);

        }
    }

    return(

        <div className = 'header'>
            <RouteSwitch></RouteSwitch>
        </div>

    )
}
export {Header}
